mapper.select:
SELECT IF(COUNT(*) > 0, true, false)
FROM daily_schedule
WHERE id = #{restaurantId}
AND day = #{dayOfWeek}
AND open_time <= #{startTime}
AND close_time >= #{endTime}
FOR SHARE

SELECT IF(COUNT(*) > 0, true, false)
FROM specific_schedule
WHERE id = #{restaurantId}
AND date = #{date}
AND open_time <= #{startTime}
AND close_time >= #{endTime}
FOR SHARE
